export default class Search extends HTMLElement{static get observedAttributes(){return["title","span","text"]}constructor(){super()}connectedCallback(){this.attachShadow({mode:"open"});const t=this.htmlTemplate(),e=document.createElement("style");e.innerHTML="\n            @import '/dist/css/reset.min.css';\n            @import '/dist/css/general.min.css';\n            @import '/dist/css/search.min.css';\n            ",this.shadowRoot.appendChild(e),this.shadowRoot.appendChild(t)}htmlTemplate(){const t=document.createElement("div");return t.setAttribute("class","search-section"),t.innerHTML=`\n        <div class="top-text">\n            <h1 id="searchTitle">${this.getAttribute("title")}</h1>\n            <p id="searchText"><span>${this.getAttribute("span")}</span>${this.getAttribute("text")}</p>\n        </div>\n        <div class="input-section">\n            <form id="searchForm">\n                <div class="input-div">\n                    <input id="emailInput" class="input-text" type="text" placeholder="email">\n                    <label id="labelInput" for="emailInput" class="input-label">email</label>\n                    <label id="errorLabel" for="emailInput">Please add a valid email address</label>\n                </div>\n                <button id="searchBtn" type="submit">go!</button>\n            </form>\n            <div class="bottom-text">\n                <img class="icon" src="/src/img/icons/icon_lock.png" alt="lock">\n                <p>Enter Any Email Address. They won't be notified</p>\n            </div>\n        </div>\n            `,t}}